import React from "react";

import "./Description.scss";

interface IProps {
    screenshotSrc: string
}

function Description({ screenshotSrc }: IProps) {
    return (
        <div id = "description" className = "description">
            <img src = {screenshotSrc} alt = "screenshot"/>
            {/* REMINDER: PLACEHOLDER SCREENSHOT ABOVE */}
            <div id = "details">
                <h2>The Problem</h2>
                <p>
                    The internet is indispensible in the modern world. For research, for email, for entertainment, for just about anything.
                    Crucially, the primary source of news in today's society is the internet. Unfortunately, many people across the world do
                    not have access to consistent internet access, particularly in second and third-world countries. But the good news is 
                    that texting plans are very cheap, and many people do have access to SMS.
                </p>
                <h2>Our Solution</h2>
                <p>
                    Millions of people across the world have access to SMS without a consistent internet connection. That's why we created
                    notify. notify sends customized and scheduled news through SMS messages. 
                </p>
            </div>
        </div>
    )
}

export default Description;
//utlizing Vonage's API for sjdfs and IBM's API for sfdjsafhkshj
