import React from "react";

import "./Description.scss";

interface IProps {
    screenshotSrc: string
}

function Description({ screenshotSrc }: IProps) {
    return (
        <div id = "description" className = "description">
            <img src = {screenshotSrc} alt = "screenshot"/>
            <div id = "details">
                <h2>The Problem</h2>
                <p>
                    The internet is indispensible in the modern world for receiving crucial news updates.
                    Unfortunately, many people across the world do not have access to consistent internet access, particularly in second 
                    and third-world countries. 
                    But the good news is 
                    that texting plans are very cheap, and many people do have texting plans without access to consistent internet access,
                    particularly in many second and third world countries.
                </p>
                <h2>Our Solution</h2>
                <p>
                    In India you can send a text for as little as $0.087CAD. The good news is that while millions of people do not have 
                    consistent internet access to view news, they often do have SMS plans. That's why we created Notify. notify sends
                    regional, customizable, and scheduled news updates through SMS messages. This way, you can stay notified no matter
                    where you are ;)
                </p>
            </div>
        </div>
    )
}

export default Description;
//utlizing Vonage's API for sjdfs and IBM's API for sfdjsafhkshj
